import React from 'react';
import './intro.css';
import bg from '../intro/p2.jpg';
import { useState, useEffect } from 'react';
 

const Intro = () => {
  const [isActive, setIsActive] = useState(false);

  useEffect(() => {
    // Activate the animation after the component mounts
    setIsActive(true);
  }, []); 

return (
  <section id="intro">
    <div className={`introcontent fade-in ${isActive ? 'active' : ''}`}>
      <h3>Hello</h3>
      <h2 className='introname'><b>I'm Pranesh Katariya</b></h2>
      <h2>Web Developer</h2>
      <p className='intropara'>I am skilled and a passionate web Developer</p>
    </div>
    <img src={bg} alt='p' className='bg' />
  </section>
);

}

export default Intro;
