import React, { useState } from 'react';
import p8 from  "../Assets/f8.jpg"


const questions = [
  {
    question: 'What is the capital of France?',
    options: [
      { image: {p8} },
    ],
    answer: 'paris.jpg'
  },
  // Add more questions with options and images here...
];

const Quiz = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState('');
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [userAnswered, setUserAnswered] = useState(false);

  const handleOptionSelect = (option) => {
    if (!userAnswered) {
      setSelectedOption(option);
    }
  };

  const handleNextQuestion = () => {
    setUserAnswered(true);
    if (selectedOption === questions[currentQuestion].answer) {
      setScore(score + 1);
    }
    if (currentQuestion < questions.length - 1) {
      setTimeout(() => {
        setCurrentQuestion(currentQuestion + 1);
        setSelectedOption('');
        setUserAnswered(false);
      }, 1000); // Delay before moving to the next question
    } else {
      setShowScore(true);
    }
  };

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', textAlign: 'center' }}>
      {showScore ? (
        <div style={{ marginBottom: '20px' }}>
          <h2>You scored {score} out of {questions.length}</h2>
        </div>
      ) : (
        <div style={{ marginBottom: '20px' }}>
          <h2>Question {currentQuestion + 1}</h2>
          <div style={{ fontSize: '20px', marginBottom: '10px' }}>
            {questions[currentQuestion].question}
          </div>
          <div style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap' }}>
            {questions[currentQuestion].options.map((option, index) => (
              <button 
                key={index} 
                style={{ 
                  margin: '10px', 
                  padding: '0', 
                  border: 'none', 
                  background: 'none', 
                  cursor: 'pointer' 
                }} 
                className={selectedOption === option.image ? 'selected' : ''} 
                onClick={() => handleOptionSelect(option.image)}
              >
                <img 
                  src={option.image} 
                  alt={`Option ${index + 1}`} 
                  style={{ 
                    width: '150px', 
                    height: '150px', 
                    borderRadius: '10px', 
                    transition: 'transform 0.3s ease',
                    transform: selectedOption === option.image ? 'scale(1.1)' : 'scale(1)'
                  }} 
                />
              </button>
            ))}
          </div>
          {selectedOption && (
            <button 
              style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px', cursor: 'pointer' }} 
              onClick={handleNextQuestion}
            >
              Next
            </button>
          )}
        </div>
      )}
    </div>
  );
};

export default Quiz;
