import React from 'react'
import { BrowserRouter as Router, Link } from 'react-router-dom'
import './Item.css'
const Item = (props) => {
  return (

    <><div className='item'>
          <Link to={`/product/${props.id}` > <img src={props.image} alt="" className='image' />} /><p>{props.name}</p><div className='item-price'>
              <div className='item-price-new'>
                  ${props.new_price}
              </div>
              <div className='item-price-old'>
                  ${props.old_price}
              </div>
          </div>
          </div>
          </>
    
 )
}

export default Item
