// server.js (Express server)
const express = require("express");
const path = require("path");

const app = express();
const PORT = 3000;

app.use(express.static(path.join(__dirname, 'build'))); // Assuming your React build files are in the 'build' directory
const customModule = require("../src/./components/Contact/contact");

app.get("/contact", (req, res) => {
  const result = customModule.Contact();
  res.send(result);
});

app.get("/about", (req, res) => {
  return res.send("Hello, this is the about page");
});

app.get("/home", (req, res) => {
  return res.send("Hello, this is the contact page");
});

app.listen(PORT, () => console.log("Server started on port", PORT));
