import './navbar.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Link } from 'react-router-dom';

import { ShoppingCart } from 'lucide-react';
import logo from '../Assets/logo.png'


const Navbar = () => {
const [menu , setMenu ] = useState("shop");
  return (
    <Router>
      <div className="navbar">
          <img src ={logo} />
          <ul className='navbar' >
          <li onClick={()=>{setMenu("shop")}}><Link to="/" className='item'>SHOP</Link>{menu==="shop"}</li>
          <li onClick={()=>{setMenu("mens")}}><Link to="/mens" className='item'>MEN</Link> {menu==="mens"}   </li>
          <li onClick={()=>{setMenu("womens")}}> <Link to="/womens" className='item'>WOMEN</Link>{menu==="womens"}    </li>
          <li onClick={()=>{setMenu("kids")}}><Link to="/kids" className='item'>KIDS</Link>  {menu==="kids"}  </li>
          </ul>
          <div className=' nav-login'>
        <Link to="/login" className='item'>LOGIN IN</Link>
        <Link to="/cart" className='item'><ShoppingCart/></Link>
          </div>
      </div>
 
    </Router>
  );
};

export default Navbar;
